function arrayIndexOf(myArray, searchTerm) {
  console.log(myArray);
  console.log(searchTerm);
  for (var i = 0, len = myArray.length; i < len; i++) {
    if (myArray[i] === searchTerm) {
      return i;
    }
  }
  return -1;
}

